int ucl_keywrap_3394_Testing(void)
{
  u8 expected_p[UCL_AES_BLOCKSIZE]={0x00,0x11,0x22,0x33,0x44,0x55,0x66,0x77,0x88,0x99,0xAA,0xBB,0xCC,0xDD,0xEE,0xFF};
  u8 key[UCL_AES_KEYLEN_128]={0x00,0x01,0x02,0x03,0x04,0x05,0x06,0x07,0x08,0x09,0x0A,0x0B,0x0C,0x0D,0x0E,0x0F};
  u8 expected_c[UCL_AES_BLOCKSIZE+UCL_AES_BLOCKSIZE/2]={0x1F,0xA6,0x8B,0x0A,0x81,0x12,0xB4,0x47,0xAE,0xF3,0x4B,0xD8,0xFB,0x5A,0x7B,0x82,0x9D,0x3E,0x86,0x23,0x71,0xD2,0xCF,0xE5};
  u8 c[UCL_AES_BLOCKSIZE+UCL_AES_BLOCKSIZE/2];
  u8 p[UCL_AES_BLOCKSIZE];
  int i;
  int resu;
  /* wrap testing */
  resu=ucl_aes_RFC3394_wrap(c,expected_p,UCL_AES_BLOCKSIZE,key);
  if(UCL_OK!=resu)
    return(UCL_ERROR);
  for(i=0;i<(int)sizeof(c);i++)
    if(c[i]!=expected_c[i])
      {
	return(UCL_ERROR);
      }
  /* unwrap testing */
  resu=ucl_aes_RFC3394_unwrap(p,expected_c,sizeof(expected_c),key);
  if(UCL_OK!=resu)
    {
      return(UCL_ERROR);
    }
  for(i=0;i<(int)sizeof(p);i++)
    if(p[i]!=expected_p[i])
      {
	return(UCL_ERROR);
      }
  return(UCL_OK);
}

int stress_aes(int maxloop)
{
  u8 plainText[AES_STRESS_SIZE], cipherText[AES_STRESS_SIZE];
  u8 decryptedText[AES_STRESS_SIZE], iv[16], aesKey[16];

  int i;

  ucl_rng_read(plainText, AES_STRESS_SIZE, UCL_RAND_DEFAULT);
  ucl_rng_read(aesKey, 16, UCL_RAND_DEFAULT);
  ucl_rng_read(iv, 16, UCL_RAND_DEFAULT);

#ifdef VERBOSE
  display_time();
#endif
  for(i=0;i<maxloop;i++)
    {
      /* ENCRYPTION */
      ucl_aes_ecb(cipherText, plainText, AES_STRESS_SIZE, aesKey, 16,UCL_CIPHER_ENCRYPT);
    }
#ifdef VERBOSE
  PRINTF("[PERFS] AES-ECB-ENC (%dKB) ",maxloop*AES_STRESS_SIZE/1024);
  display_time();
#endif
  for(i=0;i<maxloop;i++)
    {
      /* DECRYPTION */
      ucl_aes_ecb(decryptedText, cipherText, AES_STRESS_SIZE, aesKey, 16,UCL_CIPHER_DECRYPT);
    }
#ifdef VERBOSE
  PRINTF("[PERFS] AES-ECB-DEC (%dKB) ",maxloop*AES_STRESS_SIZE/1024);
  display_time();
#endif
  if( memcmp(decryptedText, plainText, 32) != 0 )
    return(UCL_ERROR);
  return(UCL_OK);
}

